mapper.select:
select sjid from PC_CBS_QUALIFICATION_INFO

    audit_tab in ('5','0','1','3')

        and sjid != #{sjid}


        and termid = #{termid}

SELECT TO_CHAR (PC_CBS_QUALIFICATION_INFO.yxq1,'YYYY') syxq1, AUDIT_TAB, sjid FROM PC_CBS_QUALIFICATION_INFO
oilfield = #{oilfield} AND AUDIT_TAB = '2'
and termid = #{termid}

SELECT * from( SELECT A.* ,rownum row_num from ( SELECT A .sjid, A .dwmc, A .dwdm, A .unitname, A .termname, A .termid, A .dwlb, A .bh, A .dwzzbh, A .jl, A .dh, A .yxq1, A .yxq2, TO_CHAR (A .YXQ1, 'YYYY-MM-DD') SYXQ1, TO_CHAR (A .YXQ2, 'YYYY-MM-DD') SYXQ2, A .ffbm, A .sgxm, A .sgqy, A .dwzzdj, A .bz, A .dwlbid, A .audit_tab, A .lrr, A .lrsj, A .shr, A .shsj, NVL(ABOLISH_TAB,'1')ABOLISH_TAB FROM PC_CBS_QUALIFICATION_INFO A
A .oilfield = #{oilfield}
and A .dwdm = #{dwdm}
and A .audit_tab = #{audit_tab}
) A ) where row_num <=#{endRow} and row_num >= #{startRow}

SELECT * from( SELECT A.* ,rownum row_num from ( SELECT A .sjid, A .dwlbid, A .dwmc, A .dwdm, A .unitname, A .termname, A .termid, A .dwlb, A .bh, A .dwzzbh, A .jl, A .dh, TO_CHAR (A .YXQ1, 'YYYY-MM-DD') SYXQ1, TO_CHAR (A .YXQ2, 'YYYY-MM-DD') SYXQ2, A .ffbm, A .sgxm, A .sgqy, A .dwzzdj, A .audit_tab, A .lrr, A .lrsj, A .shr, A .shsj FROM PC_CBS_QUALIFICATION_INFO A
A .oilfield = #{oilfield} and A .audit_tab in ('1','3')
and A .dwdm = #{dwdm}
ORDER BY A .lrsj DESC ) A ) where row_num <=#{endRow} and row_num >= #{startRow}

SELECT * from( SELECT A.* ,rownum row_num from ( SELECT TO_CHAR ( PC_CBS_QUALIFICATION_INFO.yxq1, 'YYYY' ) syxq1, AUDIT_TAB, sjid FROM PC_CBS_QUALIFICATION_INFO
A .oilfield = #{oilfield} and A .audit_tab = '2'
and termid = #{termid}
) A ) where row_num <=#{endRow} and row_num >= #{startRow}

SELECT * from( SELECT A.* ,rownum row_num from ( SELECT A .sjid, A .dwlbid, A .dwmc, A .dwdm, A .unitname, A .termname, A .termid, A .dwlb, A .bh, A .dwzzbh, A .jl, A .dh, TO_CHAR (A .YXQ1, 'YYYY-MM-DD') SYXQ1, TO_CHAR (A .YXQ2, 'YYYY-MM-DD') SYXQ2, A .ffbm, A .sgxm, A .sgqy, A .dwzzdj, A .audit_tab, A .lrr, A .lrsj, A .shr, A .shsj, A .bz FROM PC_CBS_QUALIFICATION_INFO A
A .oilfield = #{oilfield} and A .audit_tab = #{audit_tab}
and A .dwdm = #{dwdm}
ORDER BY A .lrsj DESC ) A ) where row_num <=#{endRow} and row_num >= #{startRow}

SELECT * from( SELECT A.* ,rownum row_num from ( SELECT A .sjid, A .dwlbid, A .dwmc, A .dwdm, A .unitname, A .termname, A .termid, A .dwlb, A .bh, A .dwzzbh, A .jl, A .dh, TO_CHAR (A .YXQ1, 'YYYY-MM-DD') SYXQ1, TO_CHAR (A .YXQ2, 'YYYY-MM-DD') SYXQ2, A .ffbm, A .sgxm, A .sgqy, A .dwzzdj, A .audit_tab, A .lrr, A .lrsj, A .shr, A .shsj, A .bz FROM PC_CBS_QUALIFICATION_INFO A
