and A .dwdm = #{dwdm}
ORDER BY A .lrsj DESC ) A ) where row_num <=#{endRow} and row_num >= #{startRow}

SELECT * from( SELECT A.* ,rownum row_num from ( SELECT A .sjid, A .dwmc, A .dwdm, A .unitname, A .termname, A .termid, A .dwlb, A .bh, A .dwzzbh, A .jl, A .dh, A .yxq1, A .yxq2, TO_CHAR (A .YXQ1, 'YYYY-MM-DD') SYXQ1, TO_CHAR (A .YXQ2, 'YYYY-MM-DD') SYXQ2, A .ffbm, A .sgxm, A .sgqy, A .dwzzdj, A .bz, A .dwlbid, A .audit_tab, A .lrr, A .lrsj, A .shr, A .shsj FROM PC_CBS_QUALIFICATION_INFO A
A .oilfield = #{oilfield} and A .audit_tab ='2'
and A .dwdm = #{dwdm}
and dwmc like #{dwmc}
and unitname like #{unitname}
) A ) where row_num <=#{endRow} and row_num >= #{startRow}

