mapper.update:
begin
delete from PC_CBS_QUALIFICATION_INFO where sjid = #{sjid};
delete from PC_CBS_QUALIFICATION_EQU where sjid = #{sjid};
delete from PC_CBS_QUALIFICATION_PERSON where sjid = #{sjid};
end;

	UPDATE PC_CBS_QUALIFICATION_INFO
SET AUDIT_TAB = '4'
where 
oilfield = #{oilfield}  
AND termid = #{termid} 
 AND AUDIT_TAB = '2'
