mapper.select:

	   select DISTINCT  EJDWMC contractor_name ,CONTRACTOR_ID  FROM PC_CBS_CONTRACT

        oilfield =#{oilfield}

				and PC_CBS_CONTRACT.contractor_id  =#{search}


				and PC_CBS_CONTRACT.unit_id  like #{search}||'%'


				and PC_CBS_CONTRACT.unit_id  =#{unit_id}


				and PC_CBS_CONTRACT.unit_name = #{unit_name}


				and  PC_CBS_CONTRACT.contractor_name = #{contractor_name}


				and  PC_CBS_CONTRACT.contractor_id =#{contractor_id}


   ORDER BY NLSSORT(EJDWMC,'NLS_SORT = SCHINESE_PINYIN_M')

 SELECT count(*) from PC_CBS_CONTRACT 
    LEFT JOIN (SELECT PC_CBS_CONTRACT.CONTRACT_ID,count(PC_CBS_CONTRACT.CONTRACT_ID) file_tab FROM PC_CBS_CONTRACT
    INNER JOIN PC_CBS_UPLOAD_FILES ON PC_CBS_UPLOAD_FILES.CONTRACT_ID = PC_CBS_CONTRACT.CONTRACT_ID
    where  PC_CBS_CONTRACT.oilfield=#{oilfield}   and   to_char(PC_CBS_CONTRACT.EFFECTIVE_DATE,'yyyy') = #{effective_dates} and PC_CBS_UPLOAD_FILES.FILETYPE='合同附件'

				and CONTRACTOR_ID like #{search}||'%'


				and UNIT_ID like #{search}||'%'


				and UNIT_ID = #{unit_id}


				and CONTRACTOR_ID = #{contractor_id}


			    and AUDITTAB in('1','2')


			    and AUDITTAB =#{audittab}


			    and whether_to_perform =#{whether_to_perform}


	 GROUP BY PC_CBS_CONTRACT.CONTRACT_ID ) A ON A.CONTRACT_ID= PC_CBS_CONTRACT.CONTRACT_ID
	 LEFT JOIN (SELECT PC_CBS_CONTRACT_TEAM.CONTRACT_ID ,COUNT(PC_CBS_CONTRACT_TEAM.CONTRACT_ID) term_num  FROM PC_CBS_CONTRACT
	 INNER JOIN PC_CBS_CONTRACT_TEAM ON PC_CBS_CONTRACT.CONTRACT_ID = PC_CBS_CONTRACT_TEAM.CONTRACT_ID
	     where  PC_CBS_CONTRACT.oilfield=#{oilfield}   and   to_char(PC_CBS_CONTRACT.EFFECTIVE_DATE,'yyyy') = #{effective_dates} 

				and CONTRACTOR_ID like #{search}||'%'


				and UNIT_ID like #{search}||'%'


				and UNIT_ID = #{unit_id}


				and CONTRACTOR_ID = #{contractor_id}


			    and AUDITTAB in('1','2')


			    and AUDITTAB =#{audittab}


			    and whether_to_perform =#{whether_to_perform}

	 GROUP BY PC_CBS_CONTRACT_TEAM.CONTRACT_ID )B ON B.CONTRACT_ID=PC_CBS_CONTRACT.CONTRACT_ID
	 	     where  PC_CBS_CONTRACT.oilfield=#{oilfield}   and   to_char(PC_CBS_CONTRACT.EFFECTIVE_DATE,'yyyy') = #{effective_dates} 

				and CONTRACTOR_ID like #{search}||'%'


				and UNIT_ID like #{search}||'%'


				and UNIT_ID = #{unit_id}


				and CONTRACTOR_ID = #{contractor_id}


			    and AUDITTAB in('1','2')


			    and AUDITTAB =#{audittab}


			    and whether_to_perform =#{whether_to_perform}

SELECT * from( SELECT A.* ,rownum row_num from ( SELECT AUDITTAB, PC_CBS_CONTRACT.CONTRACT_ID, CONTRACTOR_NAME, contractor_id, CONSTRUCTION_PROJECT, CONTRACT_NAME, CONTRACT_NUMBER, CONTRACT_SOURCE, to_char(OPERATION_START_DATE,'yyyy-mm-dd')operation_start_dates, to_char(COMPLETION_DATE,'yyyy-mm-dd') completion_dates, WORKLOAD, MONEY, WHETHER_TO_PERFORM, ENTRY_TIME, INPUT_PERSON, to_char(EFFECTIVE_DATE,'yyyy-mm-dd')effective_dates, UNIT_NAME, UNIT_ID , IF_BG , tax_rate, AUDITREMARK, CASE WHEN a.file_tab IS NULL THEN 0 ELSE a.file_tab END file_tab, CASE WHEN B.term_num IS NULL THEN 0 ELSE B.term_num END term_num from PC_CBS_CONTRACT LEFT JOIN (SELECT PC_CBS_CONTRACT.CONTRACT_ID,count(PC_CBS_CONTRACT.CONTRACT_ID) file_tab FROM PC_CBS_CONTRACT INNER JOIN PC_CBS_UPLOAD_FILES ON PC_CBS_UPLOAD_FILES.CONTRACT_ID = PC_CBS_CONTRACT.CONTRACT_ID where PC_CBS_CONTRACT.oilfield=#{oilfield} and to_char(PC_CBS_CONTRACT.EFFECTIVE_DATE,'yyyy') = #{effective_dates} and PC_CBS_UPLOAD_FILES.FILETYPE='合同附件'
and CONTRACTOR_ID like #{search}||'%'
and UNIT_ID like #{search}||'%'
and UNIT_ID = #{unit_id}
and CONTRACTOR_ID = #{contractor_id}
and AUDITTAB in('1','2')
and AUDITTAB =#{audittab}
and whether_to_perform =#{whether_to_perform}
GROUP BY PC_CBS_CONTRACT.CONTRACT_ID ) A ON A.CONTRACT_ID= PC_CBS_CONTRACT.CONTRACT_ID LEFT JOIN (SELECT PC_CBS_CONTRACT_TEAM.CONTRACT_ID ,COUNT(PC_CBS_CONTRACT_TEAM.CONTRACT_ID) term_num FROM PC_CBS_CONTRACT INNER JOIN PC_CBS_CONTRACT_TEAM ON PC_CBS_CONTRACT.CONTRACT_ID = PC_CBS_CONTRACT_TEAM.CONTRACT_ID where PC_CBS_CONTRACT.oilfield=#{oilfield} and to_char(PC_CBS_CONTRACT.EFFECTIVE_DATE,'yyyy') = #{effective_dates}
and CONTRACTOR_ID like #{search}||'%'
and UNIT_ID like #{search}||'%'
and UNIT_ID = #{unit_id}
and CONTRACTOR_ID = #{contractor_id}
and AUDITTAB in('1','2')
and AUDITTAB =#{audittab}
and whether_to_perform =#{whether_to_perform}
GROUP BY PC_CBS_CONTRACT_TEAM.CONTRACT_ID )B ON B.CONTRACT_ID=PC_CBS_CONTRACT.CONTRACT_ID where PC_CBS_CONTRACT.oilfield=#{oilfield} and to_char(PC_CBS_CONTRACT.EFFECTIVE_DATE,'yyyy') = #{effective_dates}
and CONTRACTOR_ID like #{search}||'%'
and UNIT_ID like #{search}||'%'
and UNIT_ID = #{unit_id}
and CONTRACTOR_ID = #{contractor_id}
and AUDITTAB in('1','2')
and AUDITTAB =#{audittab}
and whether_to_perform =#{whether_to_perform}
order by ENTRY_TIME desc ) A ) where row_num <=#{endRow} and row_num >= #{startRow}

 SELECT count(*) from PC_CBS_CONTRACT 
	 	     where  PC_CBS_CONTRACT.oilfield=#{oilfield}   and   to_char(PC_CBS_CONTRACT.EFFECTIVE_DATE,'yyyy') = #{effective_dates} 
    		 and AUDITTAB ='1' and UNIT_ID like #{search}||'%'

                    and UNIT_ID  = #{unit_id}


				and  PC_CBS_CONTRACT.contractor_id = #{contractor_id}

SELECT * from( SELECT A.* ,rownum row_num from ( SELECT AUDITTAB, PC_CBS_CONTRACT.CONTRACT_ID, CONTRACTOR_NAME, CONSTRUCTION_PROJECT, CONTRACT_NAME, CONTRACT_NUMBER, CONTRACT_SOURCE, to_char(OPERATION_START_DATE,'yyyy-mm-dd')operation_start_dates, to_char(COMPLETION_DATE,'yyyy-mm-dd') completion_dates, WORKLOAD, MONEY, WHETHER_TO_PERFORM, ENTRY_TIME, INPUT_PERSON, to_char(EFFECTIVE_DATE,'yyyy-mm-dd')effective_dates, UNIT_NAME, UNIT_ID , IF_BG , tax_rate, CASE WHEN a.file_tab IS NULL THEN 0 ELSE a.file_tab END file_tab, CASE WHEN B.term_num IS NULL THEN 0 ELSE B.term_num END term_num from PC_CBS_CONTRACT LEFT JOIN (SELECT PC_CBS_CONTRACT.CONTRACT_ID,count(PC_CBS_CONTRACT.CONTRACT_ID) file_tab FROM PC_CBS_CONTRACT INNER JOIN PC_CBS_UPLOAD_FILES ON PC_CBS_UPLOAD_FILES.CONTRACT_ID = PC_CBS_CONTRACT.CONTRACT_ID where PC_CBS_CONTRACT.oilfield=#{oilfield} and to_char(PC_CBS_CONTRACT.EFFECTIVE_DATE,'yyyy') = #{effective_dates} and PC_CBS_UPLOAD_FILES.FILETYPE='合同附件' and PC_CBS_CONTRACT.AUDITTAB ='1' and UNIT_ID like #{search}||'%'
and UNIT_ID = #{unit_id}
and PC_CBS_CONTRACT.contractor_id = #{contractor_id}
GROUP BY PC_CBS_CONTRACT.CONTRACT_ID ) A ON A.CONTRACT_ID= PC_CBS_CONTRACT.CONTRACT_ID LEFT JOIN (SELECT PC_CBS_CONTRACT_TEAM.CONTRACT_ID ,COUNT(PC_CBS_CONTRACT_TEAM.CONTRACT_ID) term_num FROM PC_CBS_CONTRACT INNER JOIN PC_CBS_CONTRACT_TEAM ON PC_CBS_CONTRACT.CONTRACT_ID = PC_CBS_CONTRACT_TEAM.CONTRACT_ID where PC_CBS_CONTRACT.oilfield=#{oilfield} and to_char(PC_CBS_CONTRACT.EFFECTIVE_DATE,'yyyy') = #{effective_dates} and PC_CBS_CONTRACT.AUDITTAB ='1' and UNIT_ID like #{search}||'%'
and UNIT_ID = #{unit_id}
and PC_CBS_CONTRACT.contractor_id = #{contractor_id}
GROUP BY PC_CBS_CONTRACT_TEAM.CONTRACT_ID )B ON B.CONTRACT_ID=PC_CBS_CONTRACT.CONTRACT_ID where PC_CBS_CONTRACT.oilfield=#{oilfield} and to_char(PC_CBS_CONTRACT.EFFECTIVE_DATE,'yyyy') = #{effective_dates} and AUDITTAB ='1' and UNIT_ID like #{search}||'%'
and UNIT_ID = #{unit_id}
and PC_CBS_CONTRACT.contractor_id = #{contractor_id}
order by ENTRY_TIME desc ) A ) where row_num <=#{endRow} and row_num >= #{startRow}

select count(*) from PC_CBS_CONTRACT INNER JOIN PC_CBS_CONTRACT_CHANGE ON PC_CBS_CONTRACT_CHANGE.CONTRACT_ID = PC_CBS_CONTRACT.CONTRACT_ID where PC_CBS_CONTRACT.oilfield=#{oilfield} and WHETHER_TO_PERFORM = '否' and PC_CBS_CONTRACT.AUDITTAB='2' and PC_CBS_CONTRACT_CHANGE.AUDITTAB = 1 and PC_CBS_CONTRACT.UNIT_ID like #{search}||'%'
and PC_CBS_CONTRACT.UNIT_ID = #{unit_id}
and PC_CBS_CONTRACT.contractor_id = #{contractor_id}

SELECT * from( SELECT A.* ,rownum row_num from (
select DISTINCT PC_CBS_CONTRACT.CONTRACT_ID, PC_CBS_CONTRACT.CONTRACTOR_NAME, PC_CBS_CONTRACT.CONSTRUCTION_PROJECT, PC_CBS_CONTRACT.CONTRACT_NAME, PC_CBS_CONTRACT.CONTRACT_NUMBER, PC_CBS_CONTRACT.CONTRACT_SOURCE, to_char(PC_CBS_CONTRACT.OPERATION_START_DATE,'yyyy-mm-dd')operation_start_dates, to_char(PC_CBS_CONTRACT.COMPLETION_DATE,'yyyy-mm-dd') completion_dates, PC_CBS_CONTRACT.WORKLOAD, PC_CBS_CONTRACT.MONEY, PC_CBS_CONTRACT.WHETHER_TO_PERFORM, PC_CBS_CONTRACT.ENTRY_TIME, PC_CBS_CONTRACT.INPUT_PERSON, to_char(PC_CBS_CONTRACT.EFFECTIVE_DATE,'yyyy-mm-dd')effective_dates, PC_CBS_CONTRACT.UNIT_NAME, PC_CBS_CONTRACT.UNIT_ID , PC_CBS_CONTRACT.IF_BG from PC_CBS_CONTRACT INNER JOIN PC_CBS_CONTRACT_CHANGE ON PC_CBS_CONTRACT_CHANGE.CONTRACT_ID = PC_CBS_CONTRACT.CONTRACT_ID where PC_CBS_CONTRACT.oilfield=#{oilfield} and WHETHER_TO_PERFORM = '否' and PC_CBS_CONTRACT.AUDITTAB='2' and PC_CBS_CONTRACT_CHANGE.AUDITTAB = 1 and PC_CBS_CONTRACT.UNIT_ID like #{search}||'%'
and PC_CBS_CONTRACT.UNIT_ID = #{unit_id}
and PC_CBS_CONTRACT.contractor_id = #{contractor_id}
order by ENTRY_TIME desc ) A ) where row_num <=#{endRow} and row_num >= #{startRow}


SELECT CONTRACT_ID,TEAM_NAME,TEAM_QUALIFICATION FROM PC_CBS_CONTRACT_TEAM
 WHERE CONTRACT_ID =#{contract_id}
 order by TERMID

mapper.update:
update PC_CBS_CONTRACT set AUDITTAB =#{audittab},
AUDITREMARK =#{auditremark},
AUDITPERSON =#{auditperson}, AUDITTIME =sysdate where PC_CBS_CONTRACT.CONTRACT_ID=#{contract_id}
