mapper.select:
select count(*) from PC_CBS_COMPANY

    oilfield =#{oilfield}

        and dwdm like #{dwdm}||'%'

SELECT * from( select k.*,ROWNUM row_num from (SELECT A.* ,CASE WHEN B.termNUM IS NULL THEN 0 ELSE B.termNUM end termNUM FROM PC_CBS_COMPANY A LEFT JOIN ( select DWDM ,count(*) termNUM from PC_CBS_QUALIFICATION_INFO WHERE AUDIT_TAB='2' GROUP BY DWDM )B ON B.DWDM =A.DWDM
oilfield =#{oilfield}
and A.dwdm = (SELECT PC_SYS_UNIT_CODE.id dwdm FROM PC_SYS_UNIT_CODE WHERE PC_SYS_UNIT_CODE.UNITNAME = #{dwmc})
and A.dwdm like #{dwdm}||'%'
ORDER BY NLSSORT (A.dwlb, 'NLS_SORT=SCHINESE_PINYIN_M' ) , NLSSORT (A.unitname, 'NLS_SORT=SCHINESE_PINYIN_M' ))k ) where row_num <=#{endRow} and row_num >= #{startRow}

SELECT * from(
select k.*,ROWNUM row_num
from
(SELECT A.* ,CASE WHEN B.termNUM IS NULL THEN 0 ELSE B.termNUM end termNUM FROM PC_CBS_COMPANY A
LEFT JOIN (
select DWDM ,count(*) termNUM from PC_CBS_QUALIFICATION_INFO
WHERE AUDIT_TAB='2'
GROUP BY DWDM )B ON B.DWDM =A.DWDM

    oilfield =#{oilfield}


        and dwmc like #{dwmc}


        and dwlbid=#{dwlbid}



        and unitname like #{unitname}


ORDER BY NLSSORT (A.dwlb, 'NLS_SORT=SCHINESE_PINYIN_M' ) , NLSSORT (A.unitname, 'NLS_SORT=SCHINESE_PINYIN_M' ))k
) where row_num <=#{endRow} and row_num >= #{startRow}

SELECT DWMC,DWDM,unitname from PC_CBS_COMPANY where oilfield=#{oilfield}

    and dwlbid=#{dwlbid}


    and unitname=#{unitname}


    and dwdm like #{dwdm}||'%'

ORDER BY NLSSORT (unitname, 'NLS_SORT=SCHINESE_PINYIN_M' )

SELECT count(*) from PC_CBS_COMPANY A

    oilfield =#{oilfield}

        and A.dwdm = (SELECT PC_SYS_UNIT_CODE.id dwdm FROM PC_SYS_UNIT_CODE WHERE PC_SYS_UNIT_CODE.UNITNAME =
        #{dwmc})


        and A.dwdm = #{dwdm}

SELECT count(*) from PC_CBS_COMPANY A

    oilfield =#{oilfield}


        and dwmc like #{dwmc}


        and dwlbid=#{dwlbid}



        and unitname like #{unitname}

SELECT * from PC_CBS_COMPANY where 1=1

    and dwlb=#{dwlb}


    and unitname=#{unitname}

ORDER BY DATATIME DESC
